import asyncio
import sys
import os

# Add project root to Python path
sys.path.append(os.path.abspath(os.path.join(os.path.dirname(__file__), '..')))

from app.mcp_server import ask_normativa, identify_person

async def test_mcp():
    print("Testing MCP Server Tools...")

    # 1. Test ask_normativa
    print("\n[1] Testing ask_normativa...")
    try:
        answer = await ask_normativa("¿Cuáles son los requisitos de admisión?")
        print(f"Result: {answer[:100]}...")
    except Exception as e:
        print(f"Error in ask_normativa: {e}")

    # 2. Test identify_person
    print("\n[2] Testing identify_person...")
    # 1x1 white pixel JPEG base64
    dummy_b64 = ""
    
    try:
        identity = await identify_person(dummy_b64)
        print(f"Result: {identity}")
    except Exception as e:
        print(f"Error in identify_person: {e}")

if __name__ == "__main__":
    asyncio.run(test_mcp())
